import React from 'react';
// import './App.css';
import CountriesList from './CountriesList'


class CountriesLibrary extends React.Component {
  constructor() {
    super()
    this.state = {
      countries: [],
      value: '',
      filter: true,
    }
  }

  fetchCountries = async () => {
    const requestReturn = await fetch('https://restcountries.eu/rest/v2/all');
    const requestData = await requestReturn.json();
    this.setState({
      contries: requestData,
    })
  }

  componentDidMount () {
    this.fetchCountries();

  }

  HandleChange = (event) => {
    this.setState({
      value: event.target.value,
      filter: false,
    })
  }

  // HandleFilter = () => {
  //   const { countries, value } = this.state
  //   // const countrieFilter = countries.filter(countrie => countrie.name.includes(value));
  //   console.log(countries.filter(countrie => countrie.name.includes(value)));
  //   // return countrieFilter;
  // }

  HandleReturn = () => {
    const { countries, value } = this.state;

    const filtro = countries.filter(countrie => countrie.name.includes(value))

    if(this.state.value) {
      <CountriesList contries={ filtro } />
    }

    return <CountriesList contries={ countries } />
  }

  render() {
    // console.log(this.state);
    // const { countries } = this.state;

    return (
      <main>
        <input  onChange={ this.HandleChange }/>
        {/* <CountriesList contries={ contries } /> */}
        { this.HandleReturn() }

      </main>

    );
  }


}

export default CountriesLibrary;
