import React from 'react';
import { Link } from 'react-router-dom';
// import './App.css';

class CountryDetails extends React.Component {
  constructor() {
    super()
    this.state = {
      country: {},
      countries: [],
      loading: true,
    }
    // this.fetchCountries = this.fetchCountries.bind(this);
  }

  // fetchCountries = async () => {
