import React from 'react';
import CountriesList from '../components/CountriesList'


class CountriesLibrary extends React.Component {
  constructor() {
    super()
    this.state = {
      countries: [],
      value: '',
    }
  }

  fetchCountries = async () => {
    const requestReturn = await fetch('https://restcountries.eu/rest/v2/all');
    const requestData = await requestReturn.json();
    this.setState({
      countries: requestData,
      value: '',
      filterInput: false,
      filterSelect: false,
    })
  }

  componentDidMount () {
    this.fetchCountries();

  }

  HandleChange = (event) => {
    this.setState({
      value: event.target.value,
      filterInput:true,
    })
  }


  // HandleSelectChange = (event) => {
  //   // const { countries } = this.state;
  //   this.setState({
  //     value: event.target.value,
  //     filterSelect:true,
  //   })
  //   console.log(this.state.value);

  //   // const countriesFilterRegion = countries.filter(country => country.region.includes(event.target.value))

  //   // return countriesFilterRegion;
  // }

  HandleReturn = () => {
    const { countries, value, filterInput, filterSelect } = this.state;

    if(filterInput) {
      const countriesFilter = countries.filter(country => country.name.includes(value))
      return <CountriesList countries={ countriesFilter } />
    }

    if(filterSelect) {
      const countriesFilterRegion = countries.filter(country => country.region.includes(value))
      return <CountriesList countries={ countriesFilterRegion } />
    }
  }



  render() {
    // console.log(this.state);
    // const { countries, value } = this.state;
    // const countriesFilter = countries.filter(country => country.name.includes(value))
    // console.log(this.HandleSelectFilter());


    return (
      <div>
        <input placeholder='Search for a country...' onChange={ this.HandleChange }/>
        {/* <FilterRegion /> */}

        <select onChange={this.HandleSelectChange }>
          <option hidden>Filter by Region</option>
          <option value="Africa" > Afr√≠ca </option>
          <option value="America"> America </option>
          <option value="Asia" > Asia </option>
          <option value="Europe"> Europe </option>
          <option value="Oceania"> Oceania </option>
       </select>

        { this.HandleReturn }
        {/* <CountriesList countries={ countriesFilter } /> */}
      </div>
    );
  }


}

export default CountriesLibrary;
