import React from 'react';
// import './App.css';
import CountriesList from './CountriesList'


class CoutriesLibrary extends React.Component {
  constructor() {
    super()
    this.state = {
      contries: [],
      value: '',
      filter: true,
    }
  }

  fetchCountries = async () => {
    const requestReturn = await fetch('https://restcountries.eu/rest/v2/all');
    const requestData = await requestReturn.json();
    this.setState({
      contries: requestData,
    })
  }

  componentDidMount () {
    this.fetchCountries();

  }

  HandleChange = (event) => {
    this.setState({
      value: event.target.value,
      filter: false,
    })
  }

  HandleFilter = () => {
    const { countries, value } = this.state
    const countrieFilter = countries.filter(countrie => countrie.name.includes(value));
    console.log(countrieFilter);
    return countrieFilter;
  }

  // HandleReturn = () => {
  //   const { contries } = this.state;

  //   if(this.state.value) {
  //     <CountriesList contries={ this.HandleFilter() } />
  //   }

  //   return <CountriesList contries={ contries } />
  // }

  render() {
    console.log(this.state);
    const { contries } = this.state;

    return (
      <main>
        <input  onChange={ this.HandleChange }/>
        <CountriesList contries={ contries } />
        {/* { this.HandleReturn() } */}

      </main>

    );
  }


}

export default CoutriesLibrary;
